using UnityEngine;

namespace App
{
    public class DamageTicker : UnityEngine.MonoBehaviour
    {
        public float amount = 5.0f;
        public App.Player player;
        public void Start()
        {
            player = GetComponent<Player>();
        }

        public void Update()
        {
            player.health -= amount * Time.deltaTime;
        }
    }
}